import React from 'react';
import { Box, Grid, FormControlLabel, Checkbox, Button, Alert, TextField, InputAdornment, IconButton, InputLabel, CircularProgress } from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useUserRegMutation } from '../../services/authApi';
import { storeToken } from '../../services/LocalStorage';

let emailRegex = /[a-z0-9]+@[a-z]+\.[a-z]{2,3}/;
const UserReg = () => {
  const Refresh = () => {
    window.location.reload();
  };

  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

  const navigate = useNavigate();
  const [userReg, { isLoading }] = useUserRegMutation();

  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      first_name: data.get('first_name'),
      last_name: data.get('last_name'),
      email: data.get('email'),
      password: data.get('password'),
      password_confirmation: data.get('password_confirmation'),
      tc: data.get('tc')
    };

    if (actualData.first_name && actualData.last_name && actualData.email && actualData.password && actualData.password_confirmation && actualData.tc !== null) {
      if (!emailRegex.test(actualData.email)) {
        setError({ status: true, msg: "Please enter a valid email address", type: 'error' });
        return;
      }
      if (actualData.password === actualData.password_confirmation) {
        const res = await userReg(actualData);
        console.log(res);
        if (res.data.status === 'success') {
          storeToken(res.data.token)
          setTimeout(() => {
            navigate('/dashboard');
          }, 3000);
        }

        if (res.data.status === 'failed') {
          console.error('error during registration', error);
          setError({ status: true, msg: res.data.message, type: 'error' });
        }
      }
      else {
        setError({ status: true, msg: "Password and Confirm Password don't match!", type: 'error' });
      }
    } else {
      setError({ status: true, msg: 'All fields are required', type: 'error' });
    }
  };

  return <>
    <h2 style={{ textAlign: 'center', color: 'navy' }}>Welcome!!!</h2>

    <Box component="form" noValidate sx={{ mt: 0, p: 1, maxWidth: '400px', margin: 'auto' }} id="reg-form" onSubmit={handleSubmit}>
      <Grid container spacing={1}>
        <Grid item xs={6}>
          <TextField margin="normal" required fullWidth id="first_name" name="first_name" label="First Name" />
        </Grid>
        <Grid item xs={6}>
          <TextField margin="normal" required fullWidth id="last_name" name="last_name" label="Last Name" />
        </Grid>
      </Grid>
      <TextField margin="normal" required fullWidth id="email" name="email" label="Email Address" />
      <TextField
        margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={handlePasswordVisibility} edge='end'>
                {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      <TextField
        margin='normal' required fullWidth id='password_confirmation' name='password_confirmation' label='Confirm Password' />
      <FormControlLabel control={<Checkbox value={true} name="tc" id="tc" />} label="I agree to the terms and conditions" />
      <Box sx={{ textAlign: 'center', mt: 1 }}>
        <Button type="submit" variant="contained" sx={{ mt: 1, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }}>
          Register
        </Button>
        <br /><br />
        <NavLink onClick={Refresh} style={{ color: 'white', fontStyle: 'normal' }}>Already have an account?</NavLink>
      </Box>
      {error.status && <Alert severity={error.type} sx={{ textAlign: 'center' }}>{error.msg}</Alert>}
    </Box>
  </>
};

export default UserReg;
