import { Box, Button, TextField } from '@mui/material';
import React, { useState } from 'react'
import { NavLink } from 'react-router-dom';

const userLogin = () => {
  const [error, setmsg]= useState({
    status: false,
    msg:"",
    type:""
  })
  const handleSubmit = (e)=>{
    e.preventDefault()
    const data= new FormData(e.currentTarget)
    const actualData={
      email: data.get('email'),
      password: data.get('password')
  
  }
  if(actualData.email && actualData.password)  {
    console.log(actualData);
  }else{
    console.log("all fields are required");
  }
  }

  return <>
  <Box component='form' noVariable sx={{ mt:1}} id='login-form' onSubmit={handleSubmit}>
    <TextField margin='normal' required fullWidth id='email' name='email' label='Email Address'/>
    <TextField margin='normal' required fullWidth id='password' name='password' type='password' label='User Password'/>
    <Box textAlign='center'>
    <Button type='submit' variant='contained' sx={{mt: 3, mb:2, px:5 }}> Login </Button>
  </Box>
  <NavLink to='/'>Forgot Password ?</NavLink>
  </Box>
  </>;
}

export default userLogin
