import React, { useState } from 'react';
import { Box, Grid, Tab, Tabs } from '@mui/material';
import background from '../../Images/background.jpg';
import UserReg from './UserReg';
import UserLogin from '../Auth/UserLogin.js';

const TabPanel = (props) => {
  const { children, value, index } = props;
  return (
    <div role='tabpanel' hidden={value !== index}>
      {value === index && <Box>{children}</Box>}
    </div>
  );
};

const LogReg = () => {
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return <>

    <Grid
     
      container
      sx={{
        backgroundImage: `url(${background})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
      }}
    >
       <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '80vh' }}>
  <h1 style={{ fontFamily: 'monospace', textAlign: 'center' }}>This is an <br/>Authentication Web Application <br/>using JSON Web Token.</h1>
</div>

      <Grid
        item
        lg={4}
        sm={6}
        sx={{
          backgroundColor: 'rgba(255, 255, 255, 0.9)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: 3,
          borderRadius: 8,
          margin: 'auto', // Center horizontally
        }}
      >
        <Box sx={{ width: '100%', maxWidth: 400 }}>
          <Tabs
            value={value}
            textColor='primary'
            indicatorColor='primary'
            onChange={handleChange}
            sx={{ marginBottom: 2 }}
          >
            <Tab label='Login' sx={{ textTransform: 'none', fontWeight: 'bold' }} />
            <Tab label='Registration' sx={{ textTransform: 'none', fontWeight: 'bold' }} />
          </Tabs>

          <TabPanel value={value} index={0}>
            <UserLogin />
          </TabPanel>
          <TabPanel value={value} index={1}>
            <UserReg />
          </TabPanel>
        </Box>
      </Grid>
    </Grid>
    </>
};

export default LogReg;
