import React, { useState } from 'react';
import { Box, Button, Alert, TextField, InputAdornment, IconButton, InputLabel, CircularProgress } from '@mui/material';
import { NavLink, useNavigate } from 'react-router-dom';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useUserLoginMutation } from '../../services/authApi';

const UserLogin = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

  const [userLogin, { isLoading }] = useUserLoginMutation()

  const [showPassword, setShowPassword] = useState(false);

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation checks (you can customize these)
    if (!formData.email || !formData.password) {
      setError({ status: true, msg: 'All fields are required', type: 'error' });
      return;
    }

    // Your additional validation logic can go here...

    console.log(formData);
    setFormData({ email: '', password: '' }); // Resetting form values using state
    setError({ status: true, msg: 'Login is successful', type: 'success' });

    setTimeout(() => {
      navigate('/dashboard');
    }, 4000);
  };

  return (
    <Box component='form' noValidate sx={{ mt: 1, p: 1, maxWidth: '400px', margin: 'auto' }} id='login-form' onSubmit={handleSubmit}>
      {/* <InputLabel shrink>Email Address</InputLabel> */}
      <TextField
        margin='normal' required fullWidth id='email' name='email' label='Email Address' value={formData.email} onChange={handleChange}
      />
      {/* <InputLabel shrink>Password</InputLabel> */}
      <TextField
        margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' value={formData.password} onChange={handleChange} InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={handlePasswordVisibility} edge='end'>
                {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      <Box sx={{ textAlign: 'center', mt: 1 }}>
        {isLoading ? <CircularProgress/>:
        <Button type='submit' variant='contained' sx={{ mt: 3, px: 5 }}>
          Login
        </Button>}
        <br />
        <NavLink to='/ResetPasswordEmail'>Forgot Password?</NavLink>
      </Box>
      {error.status && <Alert severity={error.type}>{error.msg}</Alert>}
    </Box>
  );
};

export default UserLogin;
