import React from 'react'
import { Box, TextField, Button, Alert, Grid } from '@mui/material'
import { useState } from 'react'
import { useResetPasswordEmailMutation } from '../../services/authApi'
import { useNavigate } from 'react-router-dom'
import background from '../../Images/background.jpg'

const ResetPasswordEmail = () => {
  const navigate = useNavigate();

  const emailRegex = /^\S+@(?!invalid\.com)([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,6}$/;

  const [ResetPasswordEmail, { isLoading }] = useResetPasswordEmailMutation()
  const [error, setError] = useState({
    status: false,
    msg: "",
    type: ""
  });
  const handleSubmit = async (e) => {
    e.preventDefault()
    const data = new FormData(e.currentTarget)
    const actualData = {
      email: data.get('email')

    }
    if (actualData.email) {
      if (!emailRegex.test(actualData.email)) {
        setError({ status: true, msg: "Please enter a valid email address", type: 'error' });
        return;
      }
      const res = await ResetPasswordEmail(actualData);
      console.log(actualData);

      if (res.data.status === "success") {
        document.getElementById('pass-reset-email-form').reset();
        setError({ status: true, msg: res.data.message, type: 'success' })
        setTimeout(() => {
          navigate('/');
        }, 3000);
      }
      if (res.data.status === "failed") {
        setError({ status: true, msg: res.data.message, type: 'error' })
      }
    } else {
      setError({ status: false, msg: 'The Email field is required', type: 'error' })
    }
  };
  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    minHeight: '90vh',  // Ensure the background covers the entire viewport height
  };

  return <>

    <Grid container justifyContent='center' style={containerStyle} >
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '83vh', textShadow: '3px 2px 2px white', margin: 'auto' }}>
        <h1 style={{ fontSize: '40px', fontStyle: 'italic', textAlign: 'center', color: 'navy' }}>
          This is an <br /> Authentication Web Application<br /> using JSON Web Token.
        </h1>
      </div>
      <Grid item
        lg={4}
        sm={6}
        sx={{
          position: 'relative',
          background: 'transparent',
          border: '2px solid rgba(0, 0, 0, 0.5)',
          // backgroundColor: 'rgba(255, 255, 255, 0.9)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: 3,
          borderRadius: 8,
          backdropFilter: 'blur(20px)',
          margin: 'auto',
        }}>
        <br />
        <p style={{ fontFamily: 'revert', fontSize: '15px', fontWeight: 'bold', color: 'navy' }}>Enter your registered Email to receive the  password <br />reset link !</p>
        <Box component='form' noVariable sx={{ mt: 0, width: '80%' }} id='pass-reset-email-form' onSubmit={handleSubmit}>
          <TextField required fullWidth id='email' name='email' label='Email Address' /> <br />
          <Box textAlign='center'>
            <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }}> Send </Button>
          </Box>
          {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
        </Box>
      </Grid>
    </Grid>
  </>;
}

export default ResetPasswordEmail
