import React from 'react';
import { Box, Grid, CircularProgress, Checkbox, Button, Alert, TextField, InputAdornment, IconButton, Paper } from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useState, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useUserRegMutation, useVerifyOTPMutation } from '../../services/authApi';
import { storeToken, storeAuthId, getAuthId, removeAuthId } from '../../services/LocalStorage';


const UserReg = () => {
  const Refresh = () => {
    window.location.reload();
  };

  const [userReg, { isLoading }] = useUserRegMutation();
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (error.status) {
      const timer = setTimeout(() => {
        setError(prevError => ({ ...prevError, status: false }));
      }, 3000); // Adjust the timeout duration as needed (e.g., 3000ms = 3 seconds)
      
      // Cleanup the timer on component unmount
      return () => clearTimeout(timer);
    }
  }, [error.status]);
  
  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const emailRegex = /^\S+@(?!invalid\.com)([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,6}$/;
  const passwordRegex = /^(?=.*[!@#$%^&*])(?=.*[a-zA-Z0-9]).{6,}$/;

  const handleRegistration = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const data = {
      first_name: formData.get('first_name'),
      last_name: formData.get('last_name'),
      email: formData.get('email'),
      password: formData.get('password'),
      password_confirmation: formData.get('password_confirmation'),
    };
    try {
      const response = await userReg(data).unwrap();
      if (response.status === 'success') {
        localStorage.setItem('authId', response.authid);
        navigate('/verify-otp');
      } else {
        setError(response.message);
      }
    } catch (err) {
      setError(err.data.message);
    }
  };


  return (
    <>
      <h2 style={{ textAlign: 'center', color: 'navy' }}>Welcome!!!</h2>
        <>
          <Box component="form" noValidate sx={{ mt: 0, p: 1, maxWidth: '400px', margin: 'auto' }} id="reg-form" onSubmit={handleRegistration}>
            <Grid container spacing={1}>
              <Grid item xs={6}>
                <TextField margin="normal" required fullWidth id="first_name" name="first_name" label="First Name" />
              </Grid>
              <Grid item xs={6}>
                <TextField margin="normal" required fullWidth id="last_name" name="last_name" label="Last Name" />
              </Grid>
            </Grid>
            <TextField margin="normal" required fullWidth id="email" name="email" label="Email Address" />
            <TextField
              margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
                endAdornment: (
                  <InputAdornment position='end'>
                    <IconButton onClick={handlePasswordVisibility} edge='end'>
                      {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            <TextField
              margin='normal' required fullWidth id='password_confirmation' name='password_confirmation' type='password' label='Confirm Password' />
              <NavLink onClick={Refresh} style={{ color: 'blue', fontStyle: 'normal' }}>Already have an account?</NavLink>
          </Box>
            <Box sx={{ m: 2, display: 'flex', justifyContent: 'center' }}>
              {isLoading ? <CircularProgress /> :
                <Button type='submit' variant='contained' color="info">Verify</Button>}
              {error.status && <Alert severity={error.type} sx={{ textAlign: 'center' }}>{error.msg}</Alert>}

            </Box>
        </>
    </>
  )
}

export default UserReg;
