import React from 'react'
import { Box, TextField, Button, Alert, Grid } from '@mui/material'
import { useState } from 'react'
import { useResetPasswordEmailMutation } from '../../services/authApi'
import { useNavigate } from 'react-router-dom'
import background from '../../Images/background.jpg'

const ResetPasswordEmail = () => {
  const navigate= useNavigate();

  const [ResetPasswordEmail, { isLoading }] = useResetPasswordEmailMutation()
  const [error, setError] = useState({
    status: false,
    msg: "",
    type: ""
  });
  const handleSubmit = async (e) => {
    e.preventDefault()
    const data = new FormData(e.currentTarget)
    const actualData = {
      email: data.get('email')

    }
    if (actualData.email) {
      const res = await ResetPasswordEmail(actualData); 
      console.log(actualData);

      if (res.data.status==="success") {
       document.getElementById('pass-reset-email-form').reset();
        setError({ status: true, msg: res.data.message, type: 'success' })
        setTimeout(() => {
          navigate('/');
        }, 3000);
      }
      if (res.data.status === "failed") {
        setError({ status:true, msg: res.data.message, type: 'error' })
      }
    } else {
      setError({ status: false, msg: 'The Email field is required', type: 'error' })
    }
  };
  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    minHeight: '90vh',  // Ensure the background covers the entire viewport height
  };

  return <>
    <Grid container justifyContent='center' style={{ background: 'linear-gradient(to right, #cfd9df, #e2ebf0)', minHeight: '100vh' }}>
      <Grid item lg={4} sm={6} sx={{ backgroundColor: 'rgba(255, 255, 255, 0.9)', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: 2, borderRadius: 8, margin: 'auto', mt: 4 }}>
        <h4 style={{ fontFamily: 'monospace', fontSize: '1.5rem', color: '#333' }}>Enter your registered Email to receive the password reset link!</h4>
        <Box component='form' noValidate sx={{ mt: 2 }} id='pass-reset-email-form' onSubmit={handleSubmit}>
          <TextField required fullWidth id='email' name='email' label='Email Address' />
          <Box textAlign='center' mt={2}>
            <Button type='submit' variant='contained' sx={{ mt: 2, px: 4 }}>Send</Button>
          </Box>
          {error.status && <Alert severity={error.type} sx={{ mt: 2 }}>{error.msg}</Alert>}
        </Box>
      </Grid>
    </Grid>
  </>
}

export default ResetPasswordEmail
