import React, { useState } from 'react';
import { Box, Button, Alert, TextField, InputAdornment, IconButton, InputLabel, CircularProgress, colors } from '@mui/material';
import { NavLink, useNavigate } from 'react-router-dom';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useUserLoginMutation } from '../../services/authApi';
import { storeToken } from '../../services/LocalStorage';

const UserLogin = () => {

  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

  const [userLogin, { isLoading }] = useUserLoginMutation()
  const navigate = useNavigate();

  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      email: data.get('email'),
      password: data.get('password'),
    };

    if (actualData.email && actualData.password !== null) {
        const res = await userLogin(actualData);
        console.log(res);
        if (res.data.status === 'success') {
          setError({ status: true, msg: res.data.message, type: 'success' });
          storeToken(res.data.token)
          setTimeout(() => {
            navigate('/dashboard');
          }, 3000);
        }

        if (res.data.status === 'failed') {
          console.error('error during login', error);
          setError({ status: true, msg: res.data.message, type: 'error' });
        }
      }
      else {
      setError({ status: true, msg: 'All fields are required', type: 'error' });
    }
  };

  return <>
    <h2 style={{textAlign:'center'}}>Welcome!!!</h2>
    <Box component='form' noValidate sx={{ mt: 1, p: 1, maxWidth: '400px', margin: 'auto' }} id='login-form' onSubmit={handleSubmit}>
      <TextField
        margin='normal' required fullWidth id='email' name='email' label='Email Address'/>
      <TextField
        margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={handlePasswordVisibility} edge='end'>
                {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      <Box sx={{ textAlign: 'center', mt: 1 }}>
        {isLoading ? <CircularProgress/>:
        <Button type='submit' variant='contained' sx={{ mt: 3, px: 5, backgroundColor:'white', color:'#000', fontWeight:'550'}}>
          Login
        </Button>}
        <br />
        <NavLink to='/ResetPasswordEmail'  style={{color:'wheat'}}>Forgot Password?</NavLink>
      </Box>
      {error.status && <Alert severity={error.type}>{error.msg}</Alert>}
    </Box>
    </>
};

export default UserLogin;
