import React from 'react'
import { TextField, Box, Button, NavLink } from '@mui/material'

const UserReg = () => {
    const [error, setError] = useState({
        status: false,
        msg: "",
        type: ""
      })
      const navigate = useNavigate()
      const handleSubmit = (e) => {
        e.preventDefault()
        const data = new FormData(e.currentTarget)
        const actualData = {
          email: data.get('email'),
          password: data.get('password')
    
        }
        if (actualData.email && actualData.password) {
          console.log(actualData);
          document.getElementById('login-form').reset();
          setError({ status: true, msg: "Login is successful", type: 'success' })
          navigate('/')
        } else {
          console.log("all fields are required");
        }
      }
  return (
    <Box component='form' noVariable sx={{ mt: 1 }} id='login-form' onSubmit={handleSubmit}>
    <TextField margin='normal' required halfwidth id='email' name='email' label='Email Address' /> <br/>
    <TextField margin='normal' required halfwidth id='password' name='password' type='password' label='User Password' />
    <Box textAlign='center'>
      <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5 }}> Login </Button>
    </Box>
    <NavLink to='/'>Forgot Password ?</NavLink>
    {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
  </Box>
  )
}

export default UserReg
