import React from 'react';
import { Box, Grid, FormControlLabel, Checkbox, Button, Alert, TextField, InputAdornment, IconButton, Paper } from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useUserRegMutation, useVerifyOTPMutation } from '../../services/authApi';
import { storeToken } from '../../services/LocalStorage';


const UserReg = () => {
  const Refresh = () => {
    window.location.reload();
  };

  const [authid, setAuthId] = useState(null)
  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

  const navigate = useNavigate();
  const [userReg] = useUserRegMutation();
  const [verifyOTP] = useVerifyOTPMutation();

  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const emailRegex = /^\S+@(?!invalid\.com)([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,6}$/;
  const passwordRegex = /^(?=.*[!@#$%^&*])(?=.*[a-zA-Z0-9]).{6,}$/;

  const handleSendOTP = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      first_name: data.get('first_name'),
      last_name: data.get('last_name'),
      email: data.get('email'),
      password: data.get('password'),
      password_confirmation: data.get('password_confirmation'),

    };

    if (actualData.first_name && actualData.last_name && actualData.email && actualData.password && actualData.password_confirmation) {
      if (!emailRegex.test(actualData.email)) {
        setError({ status: true, msg: "Please enter a valid email address", type: 'error' });
        return;
      }
      if (!passwordRegex.test(actualData.password)) {
        setError({ status: true, msg: "Password must be at least 6 characters long and contain at least one special character.", type: 'error' });
        return;
      }
      if (actualData.password === actualData.password_confirmation) {
        const res = await userReg(actualData);
        console.log(res);
        if (res.data.status === 'success') {
          storeToken(res.data.token);
          setAuthId(res.data.authid);
        }

        if (res.data.status === 'failed') {
          console.error('error during registration', error);
          setError({ status: true, msg: res.data.message, type: 'error' });
        }
      }
      else {
        setError({ status: true, msg: "Password and Confirm Password don't match!", type: 'error' });
      }
    } else {
      setError({ status: true, msg: 'All fields are required', type: 'error' });
    }


  };
  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      otp: data.get('otp'),
      id: authid
    };

    if (actualData.otp && actualData.id) {
      try {
        const res = await verifyOTP(actualData);
        // console.log('Response from verifyOTP:', res);
        if ( res.data.status === "success") {
          setError({ status: true, msg: res.data.message, type: 'success' });
          setTimeout(() => {
            Refresh.navigate('/LogReg');
          }, 3000);
        } else {
          setError({ status: true, msg: res.data.message, type: 'error' });
        }
      }
      catch (error) {
        console.error('Error during OTP verification:', error);
        setError({ status: true, msg: "Error occurred during OTP verification", type: 'error' });
      }
    }
  };


  return (
    <>
      <h2 style={{ textAlign: 'center', color: 'navy' }}>Welcome!!!</h2>

      {authid === null ?
        <>
          <Box component="form" noValidate sx={{ mt: 0, p: 1, maxWidth: '400px', margin: 'auto' }} id="reg-form" onSubmit={handleSendOTP}>
            <Grid container spacing={1}>
              <Grid item xs={6}>
                <TextField margin="normal" required fullWidth id="first_name" name="first_name" label="First Name" />
              </Grid>
              <Grid item xs={6}>
                <TextField margin="normal" required fullWidth id="last_name" name="last_name" label="Last Name" />
              </Grid>
            </Grid>
            <TextField margin="normal" required fullWidth id="email" name="email" label="Email Address" />
            <TextField
              margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
                endAdornment: (
                  <InputAdornment position='end'>
                    <IconButton onClick={handlePasswordVisibility} edge='end'>
                      {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            <TextField
              margin='normal' required fullWidth id='password_confirmation' name='password_confirmation' type='password' label='Confirm Password' />

            <Box sx={{ textAlign: 'center', mt: 1 }}>
              <Button type="submit" variant="contained" sx={{ mt: 1, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }}>
                get OTP
              </Button>
              {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
              <br /><br />
              <NavLink onClick={Refresh} style={{ color: 'white', fontStyle: 'normal' }}>Already have an account?</NavLink>
            </Box>
            {error.status && <Alert severity={error.type} sx={{ textAlign: 'center' }}>{error.msg}</Alert>}
          </Box>
        </>
        :
        <>
          <Box component="form" sx={{ p: 3, display: 'flex', justifyContent: 'center' }} noValidate id="verify-otp-form" onSubmit={handleVerifyOTP}>
              <TextField id="otp" name="otp" required fullWidth margin='normal' label='Enter OTP' />
              <br />
              <Box sx={{ m: 2, display: 'flex', justifyContent: 'center' }}>
              {isLoading ? <CircularProgress /> :
                <Button type='submit' variant='contained' color="info">Verify</Button>}
                {error.status && <Alert severity={error.type} sx={{ textAlign: 'center' }}>{error.msg}</Alert>}
                
              </Box>
          </Box>
        </>
      }
    </>
  )
}

export default UserReg;
