import React from 'react'
import { Box, TextField, Button, Alert, Grid } from '@mui/material'
import { useState } from 'react'
import { sendPassResetEmail, useSendPassResetEmailMutation } from '../../services/authApi'

const ResetPasswordEmail = () => {

  const [sendPassResetEmail, { isLoading }] = useSendPassResetEmailMutation()
  const [error, setError] = useState({
    status: false,
    msg: "",
    type: ""
  })
  const handleSubmit = async (e) => {
    e.preventDefault()
    const data = new FormData(e.currentTarget)
    const actualData = {
      email: data.get('email')

    }
    if (actualData.email) {
      const res = await sendPassResetEmail(actualData); 
      console.log(actualData);

      if (res.data.status === 'success') {
       document.getElementById('pass-reset-email-form').reset();
        setError({ status: true, msg: res.data.message, type: 'success' })
        setTimeout(() => {
          navigate('/');
        }, 3000);
      }
      if (res.data.status === 'failed') {
        setError({ status:true, msg: res.data.message, type: 'error' })
      }
    } else {
      setError({ status: false, msg: 'The Email field is required', type: 'error' })
    }
  }

  return <>
    <Grid container justifyContent='center'>

      <Grid item sm={6} xs={10}>
        <br />
        <h4 style={{ fontFamily: 'cursive' }}>Enter your registered Email to receive the password reset link !</h4>
        <Box component='form' noVariable sx={{ mt: 0, mx: 0 }} id='pass-reset-email-form' onSubmit={handleSubmit}>
          <TextField margin='normal' required fullWidth id='email' name='email' label='Email Address' /> <br />
          <Box textAlign='center'>
            <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5 }}> Send </Button>
          </Box>
          {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
        </Box>
      </Grid>
    </Grid>
  </>;
}

export default ResetPasswordEmail
