import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

export const authApi = createApi({
  reducerPath: 'authApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://127.0.0.1:4050/api/user/' }),

  endpoints: (builder) => ({

    userReg: builder.mutation({                      //mutation to write and modify data
      query:(user)=>{
        return{
            url: 'reg',
            method:'POST',
            body: user,
            headers:{
                'Content-type': 'application/json',
            }
        }
      }
    }),
    userLogin: builder.mutation({
      query:(user)=>{
        return{
          url: 'login',
          method: 'POST',
          body: user,
          headers:{
            'Content-type': 'application/json'
          }
        }
      }
    }),
    ResetPasswordEmail: builder.mutation({
      query:(user)=>{
        return{
          url: '/resetPasswordEmail',
          method: 'POST',
          body: user,
          headers:{
            'Content-type': 'application/json'
          }
        }
      }
    }),
    ResetPassword: builder.mutation({
      query:(actualData, _id, token)=>{
        return{
          url: `/userPasswordReset/${_id}/${token}`,
          method: 'POST',
          body: actualData,
          headers:{
            'Content-type': 'application/json'
          }
        }
      }
    }),
    getLoggedUser: builder.query({
      query:(token)=>{
        return{
          url: `/loggedUser`,
          method: 'GET',
          headers:{
            'authorization': `Bearer ${token}`,
          }
        }
      }
    })
  }),

})

export const {useUserRegMutation, useUserLoginMutation, useResetPasswordEmailMutation, useResetPasswordMutation, useGetLoggedUserQuery} =authApi
