import React from 'react'
import { Box, TextField, Button, Alert, Grid } from '@mui/material'
import { useNavigate, NavLink } from 'react-router-dom'
import { useState } from 'react'

const ResetPasswordEmail = () => {
    const [error, setError] = useState({
        status: false,
        msg: "",
        type: ""
      })
      const navigate = useNavigate()
      const handleSubmit = (e) => {
        e.preventDefault()
        const data = new FormData(e.currentTarget)
        const actualData = {
          email: data.get('email'),
          password: data.get('password')
    
        }
        if (actualData.email && actualData.password) {
          console.log(actualData);
          document.getElementById('login-form').reset();
          setError({ status: true, msg: "Login is successful", type: 'success' })
          navigate('/')
        } else {
          console.log("all fields are required");
        }
      }
    
      return <>
        <Grid container>
            <Grid item sm={6} xs={12}>
            <Box component='form' noVariable sx={{ mt: 1 }} id='pass-reset-email-form' onSubmit={handleSubmit}>
          <TextField margin='normal' required fullWidth id='email' name='email' label='Email Address' /> <br/>
          <Box textAlign='center'>
            <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5 }}> Send </Button>
          </Box>
          {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
        </Box>
            </Grid>
        </Grid>
      </>;
    }

export default ResetPasswordEmail
