import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

export const authApi = createApi({
  reducerPath: 'authApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://localhost:4050/api/user' }),
  endpoints: (builder) => ({
    userRegister: builder.mutation({                      //mutation to write and modify data
      query:(actualData)=>{
        return{
            url: 'reg',
            method:'POST',
            body: actualData,
            headers:{
                'Content-type': 'application/json',
            }
        }
      }
    }),
  }),
})

export const { useUserRegisterMutation} =authApi
