import React, { useState } from 'react';
import { Box, Button, Alert, TextField, InputAdornment, IconButton, InputLabel, CircularProgress, colors } from '@mui/material';
import { NavLink, useNavigate } from 'react-router-dom';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useUserLoginMutation,useVerifyLoginOTPMutation} from '../../services/authApi';
import { storeToken, storeAuthId, getAuthId, removeAuthId } from '../../services/LocalStorage';

const UserLogin = () => {

  const [authid, setAuthId] = useState(getAuthId())
  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

  const [userLogin] = useUserLoginMutation();
  const [verifyOTP, { isLoading }] = useVerifyLoginOTPMutation();
  const navigate = useNavigate();

  const emailRegex = /^\S+@(?!invalid\.com)([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,6}$/;
  const passwordRegex = /^(?=.*[!@#$%^&*])(?=.*[a-zA-Z0-9]).{6,}$/;

  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      email: data.get('email'),
      password: data.get('password'),
    };

    if (actualData.email && actualData.password !== null) {

      if (!emailRegex.test(actualData.email)) {
        setError({ status: true, msg: "Please enter a valid email address", type: 'error' });
        return;
      }
      if (!passwordRegex.test(actualData.password)) {
        setError({ status: true, msg: "Password must be at least 6 characters long and contain at least one special character.", type: 'error' });
        return;
      }

      const res = await userLogin(actualData);
      console.log(res);
      if (res && res.data && res.data.status === 'success'){
        storeToken(res.data.token);
        storeAuthId(res.data.authid);
        setAuthId(res.data.authid);  
        setError({ status:true, msg: res.data.message, type: 'success' });
      }

      if (res.data.status === 'failed') {
        console.error('error during login', error);
        setError({ status: true, msg: res.data.message, type: 'error' });
      }
    }
    else {
      setError({ status: true, msg: 'All fields are required', type: 'error' });
    }
  };


  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      otp: data.get('otp'),
      id: authid
    };

    if (actualData.otp && actualData.id) {
      try {
        const res = await verifyOTP(actualData);
        if (res.data.status === "success") {
          setError({ status: true, msg: res.data.message, type: 'success' });
          removeAuthId();
          setTimeout(() => {
            navigate('/');
            window.location.reload();
          },4000);
        } else {
          setError({ status: true, msg: res.data.message, type: 'error' });
        }
      }
      catch (error) {
        console.error('Error during OTP verification:', error);
        setError({ status: true, msg: "Error occurred during OTP verification", type: 'error' });
      }
    }
  };

  return (<>
    <h2 style={{ textAlign: 'center', color: 'navy' }}>Welcome!!!</h2>
    {authid === null ?
      <>
        <Box component='form' noValidate sx={{
          mt: 1, p: 1, maxWidth: '400px', margin: 'auto'
        }} id='login-form' onSubmit={handleSubmit}>
          <TextField
            margin='normal' required fullWidth id='email' name='email' label='Email Address' />
          <TextField
            margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
              endAdornment: (
                <InputAdornment position='end'>
                  <IconButton onClick={handlePasswordVisibility} edge='end'>
                    {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          <Box sx={{ textAlign: 'center', mt: 1, color: 'white' }}>
            {isLoading ? <CircularProgress /> :
              <Button type='submit' variant='contained' sx={{ mt: 3, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }}>
                Login
              </Button>}
            {error.status && <Alert severity={error.type}>{error.msg}</Alert>}
          </Box>

        </Box>
      </>
      :
      <>
        <Box component="form" sx={{ p: 3, display: 'flex', justifyContent: 'center' }} noValidate id="verify-otp-loginform" onSubmit={handleVerifyOTP}>
          <TextField id="otp" name="otp" required fullWidth margin='normal' label='Enter OTP' />
          <br />
          <Box sx={{ m: 2, display: 'flex', justifyContent: 'center' }}>

            <Button type='submit' variant='contained' color="info">Verify</Button>
            {error.status && <Alert severity={error.type} sx={{ textAlign: 'center' }}>{error.msg}</Alert>}
          </Box>
        </Box>
      </>
    }
  </>
  )
};

export default UserLogin;
