import { Alert, Box, Button, TextField } from '@mui/material';
import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';

const UserLogin = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [error, setError] = useState({
    status: false,
    msg: '',
    type: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation checks (you can customize these)
    if (!formData.email || !formData.password) {
      setError({ status: true, msg: 'All fields are required', type: 'error' });
      return;
    }

    // Your additional validation logic can go here...

    console.log(formData);
    setFormData({ email: '', password: '' }); // Resetting form values using state
    setError({ status: true, msg: 'Login is successful', type: 'success' });

    setTimeout(() => {
      navigate('/dashboard');
    }, 4000);
  };

  return (
    <>
      <Box component='form' noValidate sx={{ mt: 1 }} id='login-form' onSubmit={handleSubmit}>
        <TextField
          margin='normal'
          required
          fullWidth
          id='email'
          name='email'
          label='Email Address'
          value={formData.email}
          onChange={handleChange}
          sx={{ fontSize: '14px', mb: 1 }} // Adjusted fontSize and margin bottom
        />
        <br />
        <TextField
          margin='normal'
          required
          fullWidth
          id='password'
          name='password'
          type='password'
          label='User Password'
          value={formData.password}
          onChange={handleChange}
          pattern='(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}'
          title='Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters'
          sx={{ fontSize: '14px', mb: 2 }} // Adjusted fontSize and margin bottom
        />
        <Box component='center'>
          <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5 }}>
            Login
          </Button>
          <br />
          <NavLink to='/ResetPasswordEmail'>Forgot Password?</NavLink>
        </Box>
        {error.status && <Alert severity={error.type}>{error.msg}</Alert>}
      </Box>
    </>
  );
};

export default UserLogin;
