import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

export const authApi = createApi({
  reducerPath: 'authApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://127.0.0.1:4050/api/user/' }),

  endpoints: (builder) => ({

    userReg: builder.mutation({                      //mutation to write and modify data
      query:(user)=>{
        return{
            url: 'reg',
            method:'POST',
            body: user,
            headers:{
                'Content-type': 'application/json',
            }
        }
      }
    }),
    userLogin: builder.mutation({
      query:(user)=>{
        return{
          url: 'login',
          method: 'POST',
          body: user,
          headers:{
            'Content-type': 'application/json'
          }
        }
      }
    }),
    ResetPasswordEmail: builder.mutation({
      query:(user)=>{
        return{
          url: '/resetPasswordEmail',
          method: 'POST',
          body: user,
          headers:{
            'Content-type': 'application/json'
          }
        }
      }
    })
  }),

})

export const {useUserRegMutation, useUserLoginMutation, useResetPasswordEmailMutation} =authApi
