import React from 'react'
import {Box, Button, Alert, TextField, InputAdornment, IconButton, InputLabel, CircularProgress, colors, Grid} from '@mui/material'
import { useState } from 'react'
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useNavigate, useParams } from 'react-router-dom'
import { useUserPasswordResetMutation } from '../../services/authApi';
import background from '../../Images/background.jpg'

const ResetPass = () => {

  const [userPasswordReset, { isLoading }] = useUserPasswordResetMutation();

  const {id, token} = useParams();
  console.log(id, token);

  const [error, setError] = useState({
    status: false,
    msg: "",
    type: ""
  })

  const navigate = useNavigate()

  const passwordRegex = /^(?=.*[!@#$%^&*])(?=.*[a-zA-Z0-9]).{6,}$/;

  
  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      password: data.get('password'),
      password_confirmation: data.get('password_confirmation')
    };

    if (actualData.password && actualData.password_confirmation) {
      if (actualData.password === actualData.password_confirmation) {
        if (!passwordRegex.test(actualData.password)) {
          setError({ status: true, msg: "Password must be at least 6 characters long and contain at least one special character.", type: 'error' });
          return;
        }
        const res = await userPasswordReset({ actualData, id, token });
       
        if (res.data.status === "success") {
          document.getElementById('reset-pass-form').reset();
          setError({ status: true, msg:"password reset successfully !"})
          setTimeout(() => {
            navigate('/LogReg')
          }, 3000);
        }
      if (res.data.status === "failed"){
        setError({ status: true, msg: res.data.message, type: 'error' })

      }
      }
        else {
          setError({ status: true, msg: "password and confirm password doesn't match", type: 'error' })
        }
      
    } else {
      setError({ status: true, msg: "All fields are required", type: 'error'})
    }
  };

  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    minHeight: '90vh',  // Ensure the background covers the entire viewport height
  };

  return <>
    <Grid container justifyContent='center' style={containerStyle}>
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '83vh', textShadow: '3px 2px 2px white' ,margin: 'auto'}}>
    <h1 style={{ fontSize:'40px',fontStyle:'italic', textAlign: 'center', color: 'navy' }}>
    This is an <br/> Authentication Web Application<br/> using JSON Web Token.
  </h1>
  </div>
  <Grid item
      lg={4}
      sm={6}
      sx={{
        position: 'relative',
        background:'transparent',
        border:'2px solid rgba(0, 0, 0, 0.5)',
       // backgroundColor: 'rgba(255, 255, 255, 0.9)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        padding: 3,
        borderRadius: 8,
        backdropFilter: 'blur(20px)',
        margin: 'auto', }}>
        <br />
        <p style={{ fontFamily: 'revert' , fontSize:'22px' , fontWeight: 'bold', color:'navy'}}>Reset Password</p>
        <Box component='form' noVariable sx={{ mt: 1 }} id='reset-pass-form' onSubmit={handleSubmit}>
        <TextField
        margin='normal' required fullWidth id='password' name='password' type={showPassword ? 'text' : 'password'} label='Password' InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={handlePasswordVisibility} edge='end'>
                {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
          <TextField margin='normal' required fullWidth id='password_confirmation' name='password_confirmation'type={showPassword ? 'text' : 'password'} label='Confirm New Password' 
          InputProps={{
            endAdornment: (
              <InputAdornment position='end'>
                <IconButton onClick={handlePasswordVisibility} edge='end'>
                  {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
                </IconButton>
              </InputAdornment>
            ),
          }}/>
          <Box sx={{ textAlign: 'center', mt: 1, color: 'white' }}>
        {isLoading ? <CircularProgress /> :
          <Button type='submit' variant='contained' sx={{ mt: 3, px: 5, backgroundColor: 'white', color: 'navy', fontWeight: '550' }}>Submit</Button>}
          </Box>
     
          {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
        </Box>
      </Grid>
    </Grid>
  </>;
}

export default ResetPass;
