import React from 'react'
import { TextField, Box, Button, Alert, FormControlLabel, Checkbox } from '@mui/material'
import { useState } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'

const UserReg = () => {
    const [error, setError] = useState({
        status: false,
        msg: "",
        type: ""
    })
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()
        const data = new FormData(e.currentTarget)
        const actualData = {
            first_name: data.get('first_name'),
            last_name: data.get('last_name'),
            email: data.get('email'),
            password: data.get('password'),
            password_confirmation: data.get('password_confirmation'),
            tc: data.get('tc')

        }
        if (actualData.first_name && actualData.last_name && actualData.email && actualData.password && actualData.tc !== null) {
            if(actualData.password === actualData.password_confirmation){
                console.log(actualData);
            document.getElementById('reg-form').reset();
            setError({ status: true, msg: "Registration is successful", type: 'success' })
            setTimeout(()=>{
                navigate('/dashboard')
            }, 4000)
            }
            else{
                setError({status: true, msg:"Password and Confirm Password doesn't match !" , type:'error'})
            }
        } else {
            console.log("all fields are required");
        }
    }
    return (
        <Box component='form' noVariable sx={{ mt: 1 }} id='reg-form' onSubmit={handleSubmit} border='1rem'>
            <TextField margin='normal' required fullWidth id='first_name' name='first_name' label='First Name' />
            <TextField margin='normal' required fullWidth id='last_name' name='last_name' label='Last Name' />
            <TextField margin='normal' required fullWidth id='email' name='email' label='Email Address' />
            <TextField margin='normal' required fullWidth id='password' name='password' type='password' label=' Password' />
            <TextField margin='normal' required fullWidth id='password_confirmation' name='password_confirmation' type='password' label='Confirm Password' />
            <FormControlLabel control={<Checkbox value="agree" color='primary' name='tc' id='tc' />} label='I agree to the terms and conditions' />
            <Box component='center'>
                <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5 }}> Register </Button><br/>
                <NavLink to='/'>Already have an account ?</NavLink>
            </Box>
          
            {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
        </Box>
    )
}

export default UserReg
