import React from 'react'
import { Grid, TextField, Alert, Box, Button } from '@mui/material'
import { useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { useResetPasswordMutation } from '../../services/authApi'

const ResetPass = () => {

  const [ResetPassword, { isLoading }] = useResetPasswordMutation()
  const { _id, token } = useParams()

  const [error, setError] = useState({
    status: false,
    msg: "",
    type: ""
  })

  const navigate = useNavigate()
  
  const [showPassword, setShowPassword] = useState(false);
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const actualData = {
      password: data.get('password'),
      password_confirmation: data.get('password_confirmation')
    };

    if (actualData.password && actualData.password_confirmation) {
      if (actualData.password === actualData.password_confirmation) {

        const res = await ResetPassword({ actualData, _id, token })
        console.log(res);
        if (res.data.status === "success") {
          document.getElementById('reset-pass-form').reset();
          setError({ status: true, msg:"Password changed successfully", type: 'success' })
          setTimeout(() => {
            navigate('/LogReg')
          }, 3000);
        }
        if (res.data.status === "failed") {
          setError({ status: true, msg: res.data.message, type: 'error' })
        }
      }
      else {
        setError({ status: true, msg: " New Password and Confirm New Password doesn't match !", type: 'error' })
      }
    } else {
      console.log("all fields are required");
    }
  };

  return <>
    <Grid container justifyContent='center'
      item
      lg={4}
      sm={6}
      sx={{
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        padding: 3,
        borderRadius: 8,
        margin: 'auto' }}>
      <Grid item sm={6} xs={10}>
        <h1 >Reset Password</h1>
        <Box component='form' noVariable sx={{ mt: 1 }} id='reset-pass-form' onSubmit={handleSubmit}>
          <TextField margin='normal' required fullWidth id='password' name='password' type='password' label='New Password' />
          <TextField margin='normal' required fullWidth id='password_confirmation' name='password_confirmation' type='password' label='Confirm New Password' />
          <Box textAlign='center'>
            <Button type='submit' variant='contained' sx={{ mt: 3, mb: 2, px: 5 }}> Submit </Button>
          </Box>
          {error.status ? <Alert severity={error.type}>{error.msg}</Alert> : ''}
        </Box>
      </Grid>
    </Grid>
  </>;
}

export default ResetPass;
