import { Box, Card, Grid, Tab, Tabs } from '@mui/material'
import React, { useState } from 'react'
import background from '../../Images/background.jpg'

import UserReg from './UserReg'
import UserLogin from '../Auth/UserLogin.js'
import { Translate } from '@mui/icons-material'

const TabPanel = (props) => {
    const { children, value, index } = props;
    return (
        <div role='tabpanel' hidden={value !== index}>
            {
                value === index && (
                    <Box>{children}</Box>
                )
            }
        </div>
    )
}


const LogReg = () => {
    const [value, setValue] = useState(0);
    const handleChange = (event, newValue) => {
        setValue(newValue)
    }
    return (
        <Grid container sx={{ height: '100vh' , width: '100vw'}}>
            <Grid item lg={12} sm={6} sx={{
                backgroundImage: `url(${background})`,
                backgroundRepeat: 'no-repeat',
                backgroundSize: 'cover',
                position: 'center',
                display: { xs: 'none', sm: 'block' },
               
            }}>
                 <Grid item lg={4} sm={6} sx={{
                      backgroundColor: '#ffffff',
                      opacity: 0.1,
                    height:'cover',
                    backgroundPosition:'center',
                    backgroundSize: 'contained'
            
                 }}>
                
                    <Box sx={{mx:10, my:2 , height:600 }}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                            <Tabs value={value}  textColor='primary' indicatorColor='primary' onChange={handleChange}>
                                <Tab LinkComponent={UserLogin} label='Login' sx={{ textTransform: 'none', fontWeight: 'bold',fontSize:'19px' , fontFamily:'serif'}} ></Tab>
                                <Tab label='Registration' sx={{ textTransform: 'none', fontWeight: 'bold',fontSize:'19px',fontFamily:'serif' }}></Tab>
                            </Tabs>
                        </Box>
                        <TabPanel value={value} index={0} >
                            <UserLogin />
                        </TabPanel>
                        <TabPanel value={value} index={1} ><UserReg /></TabPanel>
                    </Box>
               
            </Grid>
            </Grid>
           
        </Grid>
    )
}

export default LogReg
